'use strict';import{MIXIN_MATCH,VAR_ASSIGN}from'./common-regex';export function updateNativeProperties(a,b){for(var c in b)null===c?a.style.removeProperty(c):a.style.setProperty(c,b[c])}export function getComputedStyleValue(a,b){var c=window.getComputedStyle(a).getPropertyValue(b);return c?c.trim():''}export function detectMixin(a){var b=MIXIN_MATCH.test(a)||VAR_ASSIGN.test(a);return MIXIN_MATCH.lastIndex=0,VAR_ASSIGN.lastIndex=0,b}